package Operators;

public class IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		    int x = 10;

		    x++;  
		    System.out.println("After increment: " + x); 

		    x--;  
		    System.out.println("After decrement: " + x); 
		    

		  }

		}

